import java.io.IOException;
import com.fasterxml.jackson.databind.JsonNode;
import com.fasterxml.jackson.databind.ObjectMapper;
import org.apache.hc.client5.http.ClientProtocolException;
import org.apache.hc.client5.http.impl.classic.CloseableHttpClient;
import org.apache.hc.client5.http.impl.classic.HttpClients;
import org.apache.hc.core5.http.ClassicHttpRequest;
import org.apache.hc.core5.http.ContentType;
import org.apache.hc.core5.http.Header;
import org.apache.hc.core5.http.HttpEntity;
import org.apache.hc.core5.http.HttpHeaders;
import org.apache.hc.core5.http.io.entity.EntityUtils;
import org.apache.hc.core5.http.io.support.ClassicRequestBuilder;

public class ApiSample {

    public static void main(String[] args) {
        String urlBase = "https://api.xdr.trendmicro.com";
        String urlPath = "/v3.0/workbench/alerts/{alertId}/notes";
        urlPath = urlPath.replace("{alertId}", "YOUR_ALERTID");
        String token = "YOUR_TOKEN";

        ClassicHttpRequest request = ClassicRequestBuilder.get(urlBase + urlPath)
                .addParameter("startDateTime", "YOUR_STARTDATETIME (string)")
                .addParameter("endDateTime", "YOUR_ENDDATETIME (string)")
                .addParameter("dateTimeTarget", "YOUR_DATETIMETARGET (string)")
                .addParameter("orderBy", "YOUR_ORDERBY (string)")
                .addParameter("top", "YOUR_TOP (integer)")
                .addHeader(HttpHeaders.AUTHORIZATION, "Bearer " + token)
                .addHeader("TMV1-Filter", "YOUR_FILTER (string)")
                .build();

        try (CloseableHttpClient httpClient = HttpClients.custom().disableContentCompression().build()) {
            httpClient.execute(request, response -> {
                HttpEntity entity = response.getEntity();
                String contentType = (entity == null) ? "" : entity.getContentType();
                long contentLength = (entity == null) ? 0 : entity.getContentLength();
                boolean isChunked = (entity == null) ? false : entity.isChunked();

                System.out.println(response.getCode());
                for (Header header: response.getHeaders()) {
                    System.out.println(header.getName() + ": " + header.getValue());
                }
                System.out.println();
                if (0 < contentLength || isChunked) {
                    if (contentType.contains("application/json")) {
                        ObjectMapper mapper = new ObjectMapper();
                        JsonNode jsonResponse = mapper.readTree(EntityUtils.toString(entity, "UTF-8"));
                        System.out.println(mapper.writerWithDefaultPrettyPrinter().writeValueAsString(jsonResponse));
                    } else {
                        System.out.println(EntityUtils.toString(entity, "UTF-8"));
                    }
                }
                return null;
            });
        } catch (ClientProtocolException e) {
            e.printStackTrace();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

